var modal = document.getElementById("myModal");
var close = document.getElementsByClassName("close")[0];
close.onclick = function () {
modal.style.display = "none";
}
window.onclick = function (event) {
if (event.target == modal) {
modal.style.display = "none";
}
}
var product_hots = document.getElementsByClassName("zoom_pro_hot");
var products = document.getElementsByClassName("zoom_pro_hot");
for (var i = 0; i < product_hots.length; i++) {
var show = product_hots[i];
show.addEventListener("click", function (event) {
var button = event.target;
var product =
button.parentElement.parentElement.parentElement.parentElement.parentElement;
var name = product.getElementsByClassName("product-title")[0].innerText
var price = product.getElementsByClassName("product-price-and-shipping")[0].innerText
var img = product.getElementsByClassName("product-img")[0].src
var des = product.getElementsByClassName("short_description")[0].innerHTML
document.getElementById("name").innerHTML = name
document.getElementById("price").innerHTML = price
document.getElementById("image").src = img
document.getElementById("description").innerHTML = des
console.log(des)
modal.style.display = "block";
})
}
var countDownDate = new Date("March 27, 2021 08:59:25").getTime();
var x = setInterval(function() {
var now = new Date().getTime();
var distance = countDownDate - now;
var days = Math.floor(distance / (1000 * 60 * 60 * 24));
var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
var seconds = Math.floor((distance % (1000 * 60)) / 1000);
document.getElementById("days").innerHTML = days
document.getElementById("hours").innerHTML = hours
document.getElementById("minutes").innerHTML = minutes
document.getElementById("seconds").innerHTML = seconds
if (distance < 0) {
clearInterval(x);
document.getElementById("ets_clock_1").innerHTML = "Đã bán";
}
}, 1000);
